import { Component } from "react";
import Formulario from "./components/formulario";
import Lista from "./components/lista";


class App extends Component {

  constructor () {
    super();
    this.lancamentos = ["Segunda", "Terça", "Quarta", "Quinta", "Sexta", "Sábado", "Domingo"];
  }

  criarLancamento(data, hora, mins) {
    console.log(data);
    console.log(hora);
    console.log(mins);


    var date = new Date(data);
    var day = date.getDate();
    var month = date.getMonth() + 1;
    var year = date.getFullYear();
    var diaMesAno = [day, month, year].join('/');

    var horaMins = hora+":"+mins;

    const lancamento = {dia: diaMesAno, lancamentos: [horaMins]};
    this.lancamentos.push(lancamento);

  }

  render () {
    return (
      <section>
        <Formulario criarLancamento={this.criarLancamento.bind(this)}/>
        <Lista lancamentos={this.lancamentos}/>
      </section>
    );
  }
}

export default App;
